mapper.select:

select 

from t_article_info
where   is_del=0  and id = #{id,jdbcType=BIGINT}


select 

from t_article_info

  is_del=0
  
     and id=#{id,jdbcType=BIGINT}
  
  
     and article_id=#{articleId,jdbcType=BIGINT}
  
  
     and writer=#{writer,jdbcType=VARCHAR}
  
  
     and publish_time=#{publishTime,jdbcType=TIMESTAMP}
  
  
     and disabled=#{disabled,jdbcType=BIT}
  
  
     and is_del=#{isDel,jdbcType=BIT}
  
  
     and create_time=#{createTime,jdbcType=TIMESTAMP}
  
  
     and create_by=#{createBy,jdbcType=BIGINT}
  
  
     and update_time=#{updateTime,jdbcType=TIMESTAMP}
  
  
     and update_by=#{updateBy,jdbcType=BIGINT}
  
  
     and body=#{body,jdbcType=LONGVARCHAR}

mapper.update:

update t_article_info  set is_del=1
where id  in  

  #{id}
