mapper.select:

select 

from t_article_list

  is_del=0
  
     and id=#{id,jdbcType=BIGINT}
  
  
     and title=#{title,jdbcType=VARCHAR}
  
  
     and url=#{url,jdbcType=VARCHAR}
  
  
     and is_reptile=#{isReptile,jdbcType=BIT}
  
  
     and disabled=#{disabled,jdbcType=BIT}
  
  
     and is_del=#{isDel,jdbcType=BIT}
  
  
     and create_time=#{createTime,jdbcType=TIMESTAMP}
  
  
     and create_by=#{createBy,jdbcType=BIGINT}
  
  
     and update_time=#{updateTime,jdbcType=TIMESTAMP}
  
  
     and update_by=#{updateBy,jdbcType=BIGINT}


select

from t_article_list
where   is_del=0  and id = #{id,jdbcType=BIGINT}

mapper.insert:

insert into t_article_list (id, title, url, 
  is_reptile, disabled, is_del, create_time, 
  create_by, update_time, update_by
  )
values (#{id,jdbcType=BIGINT}, #{title,jdbcType=VARCHAR}, #{url,jdbcType=VARCHAR}, 
  #{isReptile,jdbcType=BIT}, #{disabled,jdbcType=BIT}, #{isDel,jdbcType=BIT}, #{createTime,jdbcType=TIMESTAMP}, 
  #{createBy,jdbcType=BIGINT}, #{updateTime,jdbcType=TIMESTAMP}, #{updateBy,jdbcType=BIGINT}
  )
