mapper.select:

select 

from sys_user
where   is_del=0  and id = #{id,jdbcType=BIGINT}


select 

from sys_user

  is_del=0
  
     and id=#{id,jdbcType=BIGINT}
  
  
     and login_account=#{loginAccount,jdbcType=VARCHAR}
  
  
     and user_name=#{userName,jdbcType=VARCHAR}
  
  
     and password=#{password,jdbcType=VARCHAR}
  
  
     and sex=#{sex,jdbcType=BIT}
  
  
     and phone=#{phone,jdbcType=VARCHAR}
  
  
     and email=#{email,jdbcType=VARCHAR}
  
  
     and birth_date=#{birthDate,jdbcType=TIMESTAMP}
  
  
     and disable=#{disable,jdbcType=BIT}
  
  
     and is_del=#{isDel,jdbcType=BIT}
  
  
     and create_by=#{createBy,jdbcType=BIGINT}
  
  
     and create_time=#{createTime,jdbcType=TIMESTAMP}
  
  
     and update_by=#{updateBy,jdbcType=BIGINT}
  
  
     and update_time=#{updateTime,jdbcType=TIMESTAMP}

mapper.update:

update sys_user  set is_del=1
where id  in  

  #{id}
